INSERT INTO `post` VALUES (58, 'Cáp quang FPT Quận 3', '&lt;div class=&quot;entry-content single-page&quot;&gt;  	&lt;p style=&quot;text-align: center;&quot;&gt;&lt;span style=&quot;font-size: 18pt; color: #ff0000;&quot;&gt;&lt;strong&gt;ĐĂNG K&Yacute; DỊCH VỤ &lt;a href=&quot;https://fptcapquang.info/internet-cap-quang-fpt-2019/&quot;&gt;&lt;span style=&quot;color: #ff0000;&quot;&gt;INTERNET C&Aacute;P QUANG FPT TẠI QUẬN 3&lt;/span&gt;&lt;/a&gt; &amp;#8211; TP HỒ CH&Iacute; MINH&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt; &lt;p style=&quot;text-align: center;&quot;&gt;&lt;span style=&quot;color: #0000ff;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 14pt;&quot;&gt;&lt;a href=&quot;https://fptcapquang.info/internet-cap-quang-fpt-2019/&quot;&gt;C&aacute;p quang FPT&lt;/a&gt;, &lt;a href=&quot;https://fptcapquang.info/internet-cap-quang-fpt-2019/&quot;&gt;Wifi FPT&lt;/a&gt;, &lt;a href=&quot;https://fptcapquang.info/truyen-hinh-fpt/&quot;&gt;Truyền h&igrave;nh FPT&lt;/a&gt;, &lt;a href=&quot;https://fptcapquang.info/fpt-play-box-hd-2018/&quot;&gt;FPT Play Box&lt;/a&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt; &lt;p&gt;&lt;img class=&quot;aligncenter size-full wp-image-1907&quot; src=&quot;https://fptcapquang.info/wp-content/uploads/2018/05/logo-ftel.png&quot; alt=&quot;logo-ftel&quot; width=&quot;350&quot; height=&quot;70&quot; srcset=&quot;https://fptcapquang.info/wp-content/uploads/2018/05/logo-ftel.png 350w, https://fptcapquang.info/wp-content/uploads/2018/05/logo-ftel-300x60.png 300w&quot; sizes=&quot;(max-width: 350px) 100vw, 350px&quot; /&gt;&lt;/p&gt; &lt;p style=&quot;text-align: center;&quot;&gt;&lt;span style=&quot;color: #ff0000; font-size: 170%;&quot;&gt;&lt;strong&gt;HOTLINE : 0866 627 848&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt; &lt;p&gt;&lt;b&gt;Quận 3&nbsp;&lt;/b&gt;l&agrave; một trong những quận nội th&agrave;nh của TP Hồ Ch&iacute; Minh, l&agrave; quận nằm trong khu vực S&agrave;i G&ograve;n &amp;#8211; Bến Ngh&eacute; trước đ&acirc;y. Quận